//   sessionIdToUserMap.set(id,user);
// }

// export function getUser(id){
//   return sessionIdToUserMap.get(id);
// }



export function setUser(user){
  return jwt.sign({
    _id:user._id,
    email:user.email
  },secret);
}

export function getUser(token){
  if(!token) return null;

  try{
    return jwt.verify(token,secret);
  }catch(error){
    return null;
  }
}

